package apartado4Ejercicio3;

public class Ejercicio3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double radioCirculo = 23;
		
		double area;
		double longitud;
		
		area = Math.pow(3.14 * radioCirculo, 2);
		longitud = 3.14 * (radioCirculo * 2);
		
		System.out.println("area = " + area
				+ "\nlongitud = " + longitud);
		
	}

}
