package apartado5Ejercicio10;

public class Ejercicio10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int tamanio = 8;
		
		for(int i = 0; i < tamanio; i++) {
			for(int j = 0; j < tamanio; j++) {
				System.out.print("* ");
			}
			
			System.out.println();
		}
		
	}

}
