package apartado5Ejercicio9;

public class Ejercicio9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i = 1; i < 11; i++) {
			System.out.println("Tabla del " + i + "\n------------");
			
			for(int j = 1; j < 11; j++) {
				System.out.println(i + " x " + j + " = " + (i*j));
			}
			
			System.out.println("\n");
		}
		
	}

}
